/*Table structure for table `products` */

DROP TABLE IF EXISTS `products`;

CREATE TABLE `products` (
  `product_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 NOT NULL,
  `meta` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `keywords` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `description` text CHARACTER SET utf8,
  `color` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `consumption` varchar(1000) CHARACTER SET utf8 DEFAULT NULL,
  `package` varchar(100) CHARACTER SET utf8 DEFAULT NULL,
  `category_id` int(11) NOT NULL,
  `views` int(11) DEFAULT '0',
  `image` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `big_image` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `img_alt` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `featured` tinyint(1) DEFAULT '0',
  `storage_time` varchar(100) CHARACTER SET utf8 DEFAULT NULL,
  `durability` varchar(100) CHARACTER SET utf8 DEFAULT NULL,
  `storage_terms` varchar(1000) CHARACTER SET utf8 DEFAULT NULL,
  `use_for` varchar(1000) CHARACTER SET utf8 DEFAULT NULL,
  `date_posted` datetime DEFAULT NULL,
  `stock` tinyint(1) DEFAULT '0',
  `producer_id` int(11) NOT NULL,
  `prod_cat` int(11) DEFAULT NULL,
  `pre_packing` varchar(100) CHARACTER SET utf8 DEFAULT NULL,
  PRIMARY KEY (`product_id`)
) ENGINE=MyISAM AUTO_INCREMENT=304 DEFAULT CHARSET=latin1;

