insert  into `products`(`product_id`,`title`,`meta`,`keywords`,`description`,`color`,`consumption`,`package`,`category_id`,`views`,`image`,`big_image`,`img_alt`,`featured`,`storage_time`,`durability`,`storage_terms`,`use_for`,`date_posted`,`stock`,`producer_id`,`prod_cat`,`pre_packing`) values (71,'Грунтовка фасадная ВД-АК-0301ВГТ','','','Для наружных и внутренних работ; имеет хорошую адгезию к бетонным, кирпичным, оштукатуренным и др. минеральным поверхностям; оснижает расход краски; после высыхания образует ровную матовую полупрозрачную поверхность молочного цвета; последующее нанесение краски производится только после полного высыхания грунтовки; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','80-120 г/м2','0',135,0,'cd0d5a13.jpg',NULL,'Грунтовка фасадная ВД-АК-0301ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для выравнивания впитывающей способности и улучшения адгезии к последующим окрасочным слоям.','2010-09-28 14:03:02',0,49,36,''),(70,'Грунтовка по старой краске ВД-АК-0301 ВГТ','','','Образует на поверхности старого покрытия плёнку, улучшающую адгезию к водно-дисперсионным материалам; облегчает нанесение водно-дисперсионных красок по ранее окрашенным поверхностям; для наружных и внутренних работ; после высыхания образует ровную матовую полупрозрачную поверхность молочного цвета; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','200 г/м2','0',135,0,'0166f49d.jpg','d8b8cb56.jpg','Грунтовка по старой краске ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для предварительной обработки поверхностей, ранее окрашенных ПФ, НЦ, масляной и т.п.','2010-09-28 14:01:10',0,49,36,''),(69,'Грунтовка по металлу «Прямо по ржавчине» ВД-АК-0301 ВГТ','','','Преобразует ржавчину до 100 мкм; может использоваться как самостоятельное покрытие; после высыхания образует плотное полуматовое покрытие; обладает высокой водостойкостью; обладает хорошей твёрдостью; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','100-160 г/м2','0',135,0,'354c438e.jpg',NULL,'Грунтовка по металлу «Прямо по ржавчине» ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для подготовки поверхности металла под окраску, а при нанесении в 2-3 слоя может служить как самостоятельное покрытие.','2010-09-28 13:59:20',0,49,36,''),(68,'Грунтовка глубокого проникновения с антисептиком ВД-АК-0301ВГТ','','','Защищает поверхность от грибков, водорослей, дрожжевых бактерий, плесени; для наружных и внутренних работ; хорошая адгезия к деревянным, бетонным, кирпичным, оштукатуренным и др. видам поверхностей; глубина проникновения в основание до 5 мм; делает невидимыми высолы на кирпиче; укрепляет верхний слой бетонного пола; антисептические добавки нейтрализуют уже имеющуюся плесень и препятствуют появлению грибковых образований; создаёт на поверхностном слое на глубину проникновения \"дышащую\" водонепроницаемую плёнку; выдерживает замораживание до -15&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','80-120 г/м2','0',135,0,'ae93f3e9.jpg','2630ce9b.jpg','Грунтовка глубокого проникновения с антисептиком ВД-АК-0301ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для укрепления старых (осыпающихся) и новых поверхностей перед проведением штукатурных и малярных работ.','2010-09-28 13:56:57',0,49,36,''),(67,'Грунтовка глубокого проникновения для внутренних работ ВД-АК-0301 ВГТ','','','Обладает высокой проникающей поверхностью; улучшает адгезию; укрепляет осыпающиеся и мелящиеся старые штукатурку, краску, картон и др. пористые поверхности; после высыхания образует ровную матовую полупрозрачную поверхность; снижает расход краски; выдерживает замораживание до -15&deg;С не более одного месяца или пяти циклов замораживания-размораживания;','','80-120 г/м2','0',135,0,'58797d74.jpg','c97e2b62.jpg','Грунтовка глубокого проникновения для внутренних работ ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для укрепления пористых и сильно впитывающих минеральных поверхностей на глубину до 5 мм.','2010-09-28 13:54:57',0,49,36,''),(66,'Грунтовка глубокого проникновения ВД-АК-0301 ВГТ','','','Обладает высокой проникающей способностью; улучшает адгезию; укрепляет осыпающиеся и мелящиеся старые штукатурку, краску, картон и др. пористые поверхности; повышает износостойкость бетонных полов; после высыхания образует ровную матовую полупрозрачную поверхность; снижает расход краски; для наружных и внутренних работ; последующее нанесение краски производится только после полного высыхания грунтовки; выдерживает замораживание до -15&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','80-120 г/м2','0',135,0,'afd461ad.jpg','e5c945f8.jpg','Грунтовка глубокого проникновения ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для укрепления пористых и сильно впитывающих минеральных поверхностей на глубину до 5 мм.','2010-09-28 11:56:26',0,49,36,''),(64,'Грунтовка \"Адгезионная\" ВД-АК-0301 ВГТ','','','Образует шероховатую поверхность; снижает водопоглощение основания; обладает водостойкостью; нанесение последующих отделочных слоёв производится только после полного высыхания грунтовки; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','Может быть подколерована.','250-500 г/м2','0',135,0,'bf8d38f5.jpg','56c61d5d.jpg','Грунтовка \"Адгезионная\" ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Предназначена для предварительной обработки гладких, плотных, плохо впитывающих оснований с целью повышения сцепления с поверхностью последующих отделочных слоёв, например, декоративных штукатурок и фактурных красок.','2010-09-28 11:42:21',0,49,36,''),(65,'Грунтовка \"Бетонконтакт\" ВД-АК-0301 ВГТ','','','Образует шероховатую поверхность; улучшает сцепление последующих отделочных слоёв с поверхностью; нанесение последующих отделочных слоёв производится только после полного высыхания грунтовки; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','Может быть подколерована.','250-500 г/м2','0',135,0,'a0878d8d.jpg','ca4f3ddc.jpg','Грунтовка \"Бетонконтакт\" ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Грубодисперсная грунтовка для предварительной обработки под штукатурку плотных, гладких, плохо впитывающих оснований. Для наружных и внутренних работ.','2010-09-28 11:53:44',0,49,36,''),(62,'Герметик акриловый для наружно-внутренних работ ВГТ','','','обладает хорошими дефармационными и прочностными свойствами; устойчив к ультрафиолетовому излучению, атмосферным воздействиям, сохраняет эластичность в широком интервале температур; эксплуатация при температурах от -40&deg;С до +80&deg;С; имеет хорошую адгезию к бетону, ПВХ, алюминию, дереву, штукатурке, кирпичу, натуральному камню; гарантийный срок хранения 12 месяцев.','Белый, прозрачный','Mp=btV (кг/п.м), где: b – ширина шва (м), t – толщина слоя герметика (м), V – удельный вес (1500 кг/','0',136,0,'e353e94d.jpg','173e2109.jpg','Герметик акриловый для наружно-внутренних работ ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для герметизации монтажных швов различных строительных конструкций: окон, балконов, теплиц, стыков,','2010-09-28 11:13:59',0,49,35,''),(63,'Герметик силиконизированный для наружно-внутренних работ ВГТ','','','Надёжно защищает от влаги; отличается высокой эластичностью (25%) и прочностью; имеет хорошую адгезию к дереву, бетону, кирпичу, штукатурке, стеклу, чёрным металлам и т.д.; может окрашиваться водно-дисперсионными и пентафталевыми красками; гарантийный срок хранения 12 месяцев.','«сосна», «дуб», «бук», «махагон», «лиственница», «берёза»','Один картридж обеспечивает 10 м швов сечением 5 х 5 мм.','0',136,0,'bc1c479b.jpg','5be6236f.jpg','Герметик силиконизированный для наружно-внутренних работ ВГТ',0,'12','','Хранить в плотно закрытой таре при температуре от +4 до +30°С. Не замораживать (морозостойкость по требованию заказчика от -18°С до -40°С).','Используется для заделки швов, стыков и щелей в полу и других деревянных конструкциях.','2010-09-28 11:32:21',0,49,35,''),(72,'Шлиф-грунт ВД-АК-0301 ВГТ','','','Упрочняет основание; облегчает шлифование перед лакированием; улучшает внешний вид покрытия; после высыхания образует ровную матовую прозрачную поверхность; снижает расход лака; выдерживает замораживание до -15&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','90-120 г/м2','0',135,0,'dbb18825.jpg','e09f3590.jpg','Шлиф-грунт ВД-АК-0301 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для грунтования паркета, половой доски, мебели и других деревянных поверхностей перед лакированием.','2010-09-28 14:04:40',0,49,36,''),(73,'Клей акриловый универсальный \"Бустилат\" ВГТ','','','Образует эластичную прозрачную атмосферостойкую плёнку; имеет высокую адгезию к дереву, бетону, кирпичу, штукатурке и пр.; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','100-200 г/м2','0',122,0,'7fdc6db1.jpg','2a55ccf5.jpg','Клей акриловый универсальный \"Бустилат\" ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для приклеивания обоев, бордюров на обои (в т. ч. виниловые), плёночных материалов на бумажной и тканевой основах на стены и пол.','2010-09-28 14:07:10',0,49,37,''),(74,'Клей акриловый для облицовочный плиток ВГТ','','','Водостойкий; имеет хорошие прочностные свойства; имеет хорошую адгезию к бетону, кирпичу, стеклу и пр.; обладает очень низкой усадкой; может применяться в качестве шпатлёвки для заделки неровностей глубиной до 5 мм; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','0,8-1,5 кг/м2 сплошным слоем или 250-400 г/м2 точками.','0',122,0,'ad14a265.jpg','c799ee67.jpg','Клей акриловый для облицовочный плиток ВГТ',0,'12','','Для приклеивания керамических плиток на пол, стены и потолок, а также для приклеивания пенополистиро','Для приклеивания керамических плиток на пол, стены и потолок, а также для приклеивания пенополистирола.','2010-09-28 14:10:15',0,49,37,''),(75,'Клей акриловый для потолочных покрытий ВГТ','','','Обладает водостойкостью; имеет хорошую адгезию к дереву, бетону, кирпичу, штукатурке, стеклу и др.; может применяться в качестве высококлассной финишной шпатлёвки и для приклеивания кафельной плитки; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','0,1-0,3 кг/м2','0',122,0,'347e7633.jpg','fe2591f1.jpg','Клей акриловый для потолочных покрытий ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для приклеивания пенополистирольных и полиуретановых покрытий на потолок и стены.','2010-09-28 14:12:10',0,49,37,''),(76,'Клей акриловый напольный №1 \"Эконом\" ВГТ','','','Обладает водо- и атмосферостойкостью; имеет хорошую текучесть, что делает его удобным при работе на горизонтальных поверхностях; эластичен после полного высыхания; не отслаивается при деформациях пола; имеет хорошую адгезию к дереву, бетону, кирпичу, стеклу, пластикам и пр.; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания;','','250-400 г/м2','0',122,0,'c0b23f9c.jpg','b142f85e.jpg','Клей акриловый напольный №1 \"Эконом\" ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для наклеивания бытового линолеума и ковролина.','2010-09-28 14:13:57',0,49,37,''),(77,'Клей акриловый напольный №2 \"Профессионал\" ВГТ','','','Обладает водо- и атмосферостойкостью; имеет хорошую текучесть, что делает его удобным при работе на горизонтальных поверхностях; отличается более высокой эластичностью после полного высыхания; рекомендуется применять для офисных и гостиничных сортов линолеума; не отслаивается при деформациях пола; образует эластичную плёнку с небольшой остаточной липкостью, что улучшает адгезию к материалам с пассивной поверхностью; имеет хорошую адгезию к дереву, бетону, стеклу, пластикам и пр.; выдерживает замораживание до -18&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','250-400 г/м2','0',122,0,'40c6e38d.jpg','e95c2f18.jpg','Клей акриловый напольный №2 \"Профессионал\" ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для наклеивания линолеума (в т.ч. жёстких сортов) и ковролина.','2010-09-28 14:15:28',0,49,37,''),(78,'Клей акриловый паркетный №1 ВГТ','','','Обладает высокой водо- и атмосферостойкостью; обладает хорошей текучестью, что делает его удобным при работе на горизонтальных поверхностях; имеет высокую адгезию к дереву, бетону, кирпичу, стеклу, пластикам и пр.; имеет большую эластичность, что уменьшает вероятность отслаивания клея при деформациях пола; по согласованию с потребителем может выпускаться тонированным под определённые породы дерева; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','0,6-1 кг/м2','0',122,0,'8673d5d2.jpg','14855fa0.jpg','Клей акриловый паркетный №1 ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для наклеивания натурального паркета, паркетной доски, а также линолеума, ковролина и т.п.','2010-09-28 14:17:26',0,49,37,''),(79,'Клей акриловый ремонтно-монтажный ВГТ','','','Имеет хорошие прочностные свойства; влагоустойчив; не токсичен и не горюч; образует эластичную прозрачную плёнку; хорошая адгезия к дереву, бетону, кирпичу и пр.; для наружных и внутренних работ; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','Белый, прозрачный','300-500 г/м2','0',122,0,'e227e6fc.jpg','89ea0d17.jpg','Клей акриловый ремонтно-монтажный ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для проведения внутренних и наружных ремонтно-монтажных работ, для прочного склеивания большинства строительных материалов: стекла, дерева, МДФ, ДСП, ламината, паркета, металла, пенополистирола, гипсокартона, бетона, керамики и т.п.','2010-09-28 14:19:06',0,49,37,''),(80,'Клей ПВА \"Водостойкий\" ВГТ','','','Образует прозрачную эластичную плёнку; имеет высокую адгезию к дереву, ДСП, ДВП, бумаге и пр.; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','80-200 г/м2','0',122,0,'31439619.jpg',NULL,'Клей ПВА \"Водостойкий\" ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для ответственных столярных работ, требующих высокой прочности и водостойкости клеевого шва. Имеет высокую устойчивость к воздействию воды (степень водостойкости соответствует стандарту D3).','2010-09-28 14:40:41',0,49,38,''),(81,'Клей ПВА \"Профи\"  ВГТ','','','Образует прозрачную эластичную плёнку; имеет высокую адгезию к дереву, ДСП, ДВП, бумаге и пр.; выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-размораживания.','','100-200 г/м2','0',122,0,'da3aaf34.jpg',NULL,'Клей ПВА \"Профи\"  ВГТ',0,'12','','В плотно закрытой таре при температуре от 0°С до +30°С.','Для ответственных столярных работ, требующих высокой прочности. Рекомендуется для использования в мебельном производстве.','2010-09-28 14:43:14',0,49,38,''),(82,'Клей ПВА строительный ВГТ','','','Образует эластичную плёнку; улучшает реологию строительных смесей; повышает прочность сцепления с поверхностью; Для склейки ответственных столярных изделий или несущих конструкций рекомендуются другие более высококлассные клеи нашей фирмы: ПВА &laquo;Экстра&raquo;, ПВА &laquo;Профи&raquo;, ПВА &laquo;Водостойкий&raquo;. выдерживает замораживание до -40&deg;С не более одного месяца или пяти циклов замораживания-разм